import React, {useState, useEffect} from 'react';
import './App.css';
import Axios from 'axios'
import Form from 'react-jsonschema-form';

const schema = `{
  "type": "object",
  "tablename": "People",
  "properties": {
    "name": {
      "type": "string"
    },
    "age": {
      "type": "integer",
      "minimum": 10,
      "maximum": 100
    },
    "married": {
      "type": "boolean",
      "default": false
    }
  },
  "required": [
    "name",
    "age",
    "married"
  ]
}`;

const schemaAsObject = JSON.parse(schema);

function App() {

  const submit = ({formData}, e) => console.log("Data submitted: ",  formData);
  const error = (errors) => alert("Error in submission");

  return (
    <div className="App">
      <h1>Dynamic database Vitec</h1>
      <div className='form'>
        <Form schema={schemaAsObject}
          onSubmit = {submit}
          onError = {error} />,
      </div>
    </div>
  );
}

export default App;
