import React, {useEffect, useState} from 'react';
import './App.css';
import Axios from 'axios'
import Form from 'react-jsonschema-form';
import DynamicTable from "./dynamictable";

const schema = `{
  "type": "object",
  "tablename": "People",
  "properties": {
    "name": {
      "type": "string"
    },
    "age": {
      "type": "integer",
      "enum": [1,2,3]
    },
    "married": {
      "type": "boolean",
      "default": false
    }
  },
  "required": [
    "name",
    "age",
    "married"
  ]
}`;

const schemaAsObject = JSON.parse(schema);

function App() {

  const [dbList, setDBList] = useState([])

  useEffect(() => {
    Axios.get("http://localhost:3001/getelements").then((response) => {
      setDBList(response.data);
    })
  }, [])

  const submit = ({formData}, e) => {
    Axios.post("http://localhost:3001/insert", {data: formData}).then((response) => {
      console.log(formData)
    })
  }

  const error = (errors) => alert("Error");

  return (
    <div className="App">
      <h1>Dynamic database Vitec</h1>
      <div className='form'>
        <Form schema={schemaAsObject}
          onSubmit = {submit}
          onError = {error} />,
      </div>
      <DynamicTable sqlData={dbList}/>    
    </div>
  );

}

export default App;
