import React from "react";
import "./dynamictable.css"
import schema from "../template"

const schemaAsObject = JSON.parse(schema);

const edit = ({index}, e) => {
    Axios.post(`http://localhost:3001/edit/`, {data: formData, template: schemaAsObject}).then((response) => {
      alert(response.data)
    })
  }

const deleteElement = ({index}, e) => {
    Axios.post(`http://localhost:3001/edit/${index}`).then((response) => {
        alert(response.data)
    })
}



const DynamicTable = ({sqlData}) => {

    let columns = []

    for(let i in schemaAsObject.properties){
        columns.push(i)
    }

    console.log(columns)

    return (
        <table>
            <thead>
                <tr>
                    {columns.map((item, index) => <ColumnName item = {item}/>)}
                </tr>
            </thead>
            <tbody>
                {sqlData.map((item, index) => <ColumnValues item = {item} columns = {columns} />)}
            </tbody>
        </table>
    )

}

const ColumnName = ({item}) => <th>{item}</th>
const ColumnValues = ({item, columns, index}) => (
    <tr>
        {columns.map((columnName, index) => {
            return <td> {item[`${columnName}`]} </td>
        })}
        <button onClick = {edit}>Edit</button>
        <button onClick = {deleteElement}>Delete</button>
    </tr>
)

export default DynamicTable;
