import React, {useState, useEffect} from 'react';
import './App.css';
import Axios from 'axios'
import Form from 'react-jsonschema-form';

const schema = `{
  "type": "object",
  "tablename": "People",
  "properties": {
    "name": {
      "type": "string"
    },
    "age": {
      "type": "integer",
      "minimum": 10,
      "maximum": 100
    },
    "married": {
      "type": "boolean",
      "default": false
    }
  },
  "required": [
    "name",
    "age",
    "married"
  ]
}`;

const schemaAsObject = JSON.parse(schema);

function App() {

  const [movieName, setMovieName] = useState("");
  const [review, setReview] = useState("");

  const submitElement = () => {
    Axios.post("http://localhost:3001/api/insert", {
      movieName: movieName,
      review: review,
    }).then(() => {
      console.log("Succesful insert")
    })
  }

  return (
    <div className="App">
      <h1>Dynamic database</h1>
      <div className='form'>

        <label>Movie Name</label>
        <input 
          type="text" 
          name="Moviename" 
          onChange={(e) => {
            setMovieName(e.target.value)
          }}
        />

        <label>Review</label>
        <input 
          type="text" 
          name="review" 
          onChange={(e) => {
            setReview(e.target.value)
            }}
        />

        <button onClick={submitElement}>Submit</button>
      </div>
    </div>
  );
}

export default App;
