import React, {Component} from 'react';
import { Field } from './component'

class Form extends Component {
    state = {
        fields: [
            {
               id: 'name',
               type: 'input',
               placeholder: 'Enter name',
               config: {
                   dataType: 'text',
                   placeholder: 'Your Name'
               },
               value: '',
               validation: {
                   required: true
               },
               valid: false, 
               errorMessage: 'Enter Valid data',
               touched: false
           },
              {
               id: 'ID',
               type: 'textarea',
               placeholder: 'Enter ID',
               config: {
                   dataType: 'int',
                   placeholder: 0
               },
               value: 0,
               validation: {
                   minLength: 6,
                   maxLength: 6,
                   required: true
               },
               valid: false,
               errorMessage: 'Enter Valid ID',
               touched: false
           },
            {
               id: 'Birth date',
               type: 'input',
               placeholder: 'Enter birthdate',
               config: {
                   dataType: 'text',
                   placeholder: 'dd/mm/yyyy'
               },
               value: '',
               validation: {
                   required: true,
                   minLength: 10,
                   maxLength: 10
               },
               valid: false,
               errorMessage: 'Enter Valid data',
               touched: false
           },
            {
               id: 'email',
               type: 'input',
               placeholder: 'Enter email',
               config: {
                   dataType: 'email',
                   placeholder: 'Your E-Mail'
               },
               value: '',
               validation: {
                   required: true,
                   isEmail: true
               },
               valid: false,
               errorMessage: 'Enter Valid data',
               touched: false
           },
           {
                id: 'country',
                type: 'select',
                placeholder: 'Enter country',
                config: {
                    dataType: 'text',
                    placeholder: 'Country'
                },
                options: [
                    {
                        value: 'USA',
                        displayValue: 'United state of america'
                    },
                    {
                        value: 'UK',
                        displayValue: 'United kingdom'
                    },
                    {
                        value: 'FIN',
                        displayValue: 'Finland'
                    }
                ],
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                errorMessage: 'Enter Valid data',
                touched: false
            }
        ],
       formValid: false
    }
   
    fieldChange = (event,field,index) => {
        const updatedField = {...field};
        updatedField.value = event.target.value;
        updatedField.valid = this.checkValidity(updatedField);
        
        const updatedFields =  [...this.state.fields];
        updatedFields.splice(index,1,updatedField  );
        let formValid = true;
        for(let field of updatedFields){
           if(!field.valid){
              formValid = false;
           }
       }
        this.setState({
            fields: updatedFields,
            formValid: formValid
        })
   
   
       console.log('field changed')
    }
   
   
    checkValidity = (field) =>{
        const rules = field.validation;
        const value = field.value;
        let isValid = true;
       if (!rules) {
           return true;
       }
   
       
       if (rules.required) {
           isValid = value.trim() !== '' && isValid;
       }
   
       if (rules.minLength) {
           isValid = value.length >= rules.minLength && isValid
       }
   
       if (rules.maxLength) {
           isValid = value.length <= rules.maxLength && isValid
       }
   
       if (rules.pattern) {
           isValid = rules.pattern.test(value) && isValid
       }
    
    
   
       return isValid;
    }
   
    fieldBlur(event,field,index){
        if(field.touched){
            return;
        }
       const updatedField = {...field};
        updatedField.touched = true;
        updatedField.valid = this.checkValidity(updatedField);
        const updatedFields =  [...this.state.fields];
        updatedFields.splice(index,1,updatedField);
        
      
        this.setState({
            fields: updatedFields,
        })
   
   
    }
    onSubmit = (event) =>{
       event.preventDefault();
       alert('data submitted')
    }
   
       render(){
          return (<form onSubmit={(event)=>this.onSubmit(event)}>
               {this.state.fields.map((field,index)=>{
                   return <Field 
                    key={field.id}
                    fieldConfig={field}
                    focused={(event)=>this.fieldBlur(event,field,index)} 
                    changed={(event)=>this.fieldChange(event,field,index)} />
               })}
               <button type='submit' disabled={!this.state.formValid}>Submit</button>
           </form>)
   
       }
   }
   
   export default Form;
