import React, {useEffect, useState} from 'react';
import './App.css';
import Axios from 'axios'
import Form from 'react-jsonschema-form';
import DynamicTable from "./dynamictable/dynamictable";
import schema from "./template"

const CheckTable = (dbList) => {
    let res 
    Axios.post("http://localhost:3001/exists", {template: schemaAsObject}).then((response) => {res = response})
    if(Array.isArray(res.data)){
      return <DynamicTable sqlData={dbList}/>
    }else{
      return <div></div>
    }


}

const schemaAsObject = JSON.parse(schema);

function App() {

  const [dbList, setDBList] = useState([])

  useEffect(() => {
    Axios.post("http://localhost:3001/getelements", {template: schemaAsObject}).then((response) => {
      setDBList(response.data);
    })
  }, [])

  const createtable = () => {
    Axios.post("http://localhost:3001/createtable", {template: schemaAsObject}).then((response) => {
      alert(response.data)
    })
  }

  const submit = ({formData}) => {
    Axios.post("http://localhost:3001/insert", {data: formData, template: schemaAsObject}).then((response) => {
      alert(response.data)
    })
  }

  const error = (errors) => alert("Error");

  return (
    <div className="App">
      <h1>Dynamic database Vitec</h1>
      <div className='form'>
        <Form schema={schemaAsObject}
          onSubmit = {submit}
          onError = {error} />,
      </div>
      <div>
        <CheckTable dbList={dbList}/>
      </div>
      <div>
        <button onClick = {createtable}>Create Table</button>
      </div>
    </div>
  );

}

export default App;
